source.scss:
/* #! scss */

$pi: 0;
@for $i from 0 through 100 {
    $t: ($i * 2) + 1;
    @if $i % 2 == 0 {
	    $pi: $pi + 4 / $t;
    } @else {
	    $pi: $pi - 4 / $t;
    }
}

li {
    list-style-type: none;
}
li:after {
    border-radius: 200px;
    width: 40px;
    height: 40px;
}

@for $i from 1 through 12 {
    li:nth-child(#{$i}) {

       &:after {
		   background-color: hsl(30deg * ($i - 1), 100%, 50%);
           $x: (($i - 7) / 12) * $pi * 2;
		   $sin: ($x) - ($x * $x * $x) / 6 + ($x * $x * $x * $x * $x) / 120 - ($x * $x * $x * $x * $x * $x * $x) / 5040;
		   $cos: 1 - ($x * $x) / 2 + ($x * $x * $x * $x) / 24 - ($x * $x * $x * $x * $x * $x) / 720;
           content: '';
           position: absolute;
           left: 100px * $cos + 200;
           top: 100px * $sin + 200;
           color: hsl(30deg * (($i - 1 + 6) % 12), 100%, 40%);
       }
    }
}
