source.css:
body {
  background: #f5f3fa;
  overflow: hidden;
}

a {
  position: absolute;
}

li {
  list-style-type: none;
}

@-webkit-keyframes blow {
   0.0%   {-webkit-transform: translate(0px, 0px);}
  25.0%   {-webkit-transform: translate(-200px, 0px);}
  50.0%   {-webkit-transform: translate( 200px, 0px);}
 100.0%   {-webkit-transform: translate(0px, 0px);}
}
.wind {  
  -webkit-animation-name: blow;
  -webkit-animation-timing-function: ease-in-out;
  -webkit-animation-duration: 20s;
  -webkit-animation-direction: normal;
  -webkit-animation-iteration-count: infinite;
}


@-webkit-keyframes fall {
   0.0%   {-webkit-transform: translate(0px, -330px) rotate(0deg);}
   100%   {-webkit-transform: translate(0px, 600px) rotate(180deg);}
}


.snow {
  color: #f5f3fa;
  text-shadow: 0px 0px 10px #76b;

  -webkit-animation-name: fall;
  -webkit-animation-timing-function: linear;
  -webkit-animation-duration: 10s;
  -webkit-animation-direction: normal;
  -webkit-animation-iteration-count: infinite;
}

.snow:after {
  content: '*';
}

