html, body, h1, h2, h3, h4, h5, h6, ul, ol, dl, li, dt, dd, p,
header, hgroup, section, article, aside, hgroup, footer, figure, figcaption, nav {
    position: relative;
    margin: 0;
    padding: 0;
    font-size: 100%;
    font-weight: normal;
    font-family: "ヒラギノ角ゴ Pro W3", "Hiragino Kaku Gothic Pro", "メイリオ", Meiryo, Osaka, "ＭＳ Ｐゴシック", "MS PGothic", sans-serif;
}
ul, ol, dl, li, dt, dd{
    list-style: none;
}
body {
    position: relative;
    width: 100%;
    line-height: 1.0;
    background-color: #a6a6a6;
    -webkit-text-size-adjust: none;
    text-align: center;
}

#bossEffect{
    position: relative;
    margin: 0 auto;
    width: 320px;
    height:480px;
	background: -webkit-gradient(radial, center center, 0, right top, 500,
	            from(#000),
				to(#006));
    -webkit-background-size: 100% auto;
    background-size: 100% auto;
    overflow:hidden;
}

#box1 {
	position: absolute;
	width: 20px;
	height: 20px;
	left: 0px;
	top: 0px;
	border: solid 2px #622;
    -webkit-animation-name: 'box1_anime';
	-webkit-animation-duration: 2.5s;
	-webkit-animation-delay: 0s;
	background: -webkit-gradient(linear, left bottom, left top, 
		color-stop( 0, #300),
		color-stop(.5, #411),
		color-stop( 1, #200));

	opacity: 0.0;
}

#logo1 {
	width: 100%;
	height: 100%;
	background: url(http://jsdo.it/img/event/spec/vol4/sample/logo1.png) center top no-repeat;
	opacity: 0.3;
}

@-webkit-keyframes box1_anime {
    0% {-webkit-transform:translate( 30px, 600px) rotate(180deg); opacity: 0.1;}
   15% {-webkit-transform:translate( 30px, 18px)  rotate(-20deg); opacity: 0.5;}
   20% {-webkit-transform:translate( 30px, 15px)  rotate( 10deg); opacity: 0.5;}
   25% {-webkit-transform:translate( 30px, 20px); opacity: 0.9; width: 20px; height: 20px;}
   34% {-webkit-transform:translate( 30px, 20px); opacity: 0.9; width:240px; height: 20px;}
   35% {-webkit-transform:translate( 30px, 20px); opacity: 0.9; width:240px; height: 20px;}
   45% {-webkit-transform:translate( 30px, 20px); opacity: 0.9; width:240px; height:160px;}
   64% {-webkit-transform:translate( 30px, 20px); opacity: 0.9; width:240px; height:160px;}
   65% {-webkit-transform:translate( 30px, 20px); opacity: 0.0; width:240px; height:160px;}
   66% {-webkit-transform:translate( 30px, 20px); opacity: 0.9; width:240px; height:160px;}
   67% {-webkit-transform:translate( 30px, 20px); opacity: 0.0; width:240px; height:160px;}
   68% {-webkit-transform:translate( 30px, 20px); opacity: 0.9; width:240px; height:160px;}
   69% {-webkit-transform:translate( 30px, 20px); opacity: 0.0; width:240px; height:160px;}
   70% {-webkit-transform:translate( 30px, 20px); opacity: 0.9; width:240px; height:160px;}
   90% {-webkit-transform:translate( 30px, 20px)  rotate( 90deg); opacity: 0.9; width: 20px; height: 20px;}
  100% {-webkit-transform:translate( 30px, 600px) rotate(360deg); opacity: 0.1; width: 20px; height: 20px; }
}

#box2 {
	position: absolute;
	width: 20px;
	height: 20px;
	left: 0px;
	top: 0px;
	border: solid 2px #33c;
    -webkit-animation-name: 'box2_anime';
	-webkit-animation-duration: 2.2s;
	-webkit-animation-delay: 0.2s;
	background: -webkit-gradient(linear, left bottom, left top, 
		color-stop( 0, #003),
		color-stop(.5, #114),
		color-stop( 1, #001));

	opacity: 0.0;
}

#logo2 {
	width: 100%;
	height: 100%;
	background: url(http://jsdo.it/img/event/spec/vol4/sample/logo2.png) center top no-repeat;
	opacity: 0.3;
}

@-webkit-keyframes box2_anime {
    0% {-webkit-transform:translate(280px,   0px); opacity: 0.1;}
   15% {-webkit-transform:translate(280px, 308px) rotate(-20deg); opacity: 0.5;}
   20% {-webkit-transform:translate(280px, 305px) rotate( 10deg); opacity: 0.5;}
   25% {-webkit-transform:translate(280px, 300px); opacity: 0.9; width: 20px; height: 20px;}
   34% {-webkit-transform:translate( 60px, 300px); opacity: 0.9; width:240px; height: 20px;}
   35% {-webkit-transform:translate( 60px, 300px); opacity: 0.9; width:240px; height: 20px;}
   45% {-webkit-transform:translate( 60px, 300px); opacity: 0.9; width:240px; height:160px;}
   64% {-webkit-transform:translate( 60px, 300px); opacity: 0.9; width:240px; height:160px;}
   65% {-webkit-transform:translate( 60px, 300px); opacity: 0.0; width:240px; height:160px;}
   66% {-webkit-transform:translate( 60px, 300px); opacity: 0.9; width:240px; height:160px;}
   67% {-webkit-transform:translate( 60px, 300px); opacity: 0.0; width:240px; height:160px;}
   68% {-webkit-transform:translate( 60px, 300px); opacity: 0.9; width:240px; height:160px;}
   69% {-webkit-transform:translate( 60px, 300px); opacity: 0.0; width:240px; height:160px;}
   70% {-webkit-transform:translate( 60px, 300px); opacity: 0.9; width:240px; height:160px;}
   90% {-webkit-transform:translate(200px, 300px) rotate(-90deg); opacity: 0.9; width: 20px; height: 20px;}
  100% {-webkit-transform:translate(180px, 0px) rotate(360deg); opacity: 0.1; width: 20px; height: 20px; }
}

#vs {
	font-family: cursive;
	font-size: 60px;
	font-style: italic;
	color: #f33;
	position: relative;
	opacity: 0;
	text-shadow: 2px 10px 20px #f00,
	             10px 2px 20px #996;
	-webkit-animation-name: 'vs_anime';
	-webkit-animation-duration: 2.0s;
	-webkit-animation-timing-function: cubic-bezier(.01,.89,1,.02);
	-webkit-animation-delay: 0.3s;
}

@-webkit-keyframes vs_anime {
	 0% {-webkit-transform:translate( 300px, 10px); opacity: 0.9;}
	19% {-webkit-transform:translate( 250px, 10px); opacity: 0.0;}
	20% {-webkit-transform:translate( 250px, 10px); opacity: 0.9;}
	22% {-webkit-transform:translate( 260px, 10px); opacity: 0.0;}
	24% {-webkit-transform:translate( 270px, 10px); opacity: 0.9;}
   100% {-webkit-transform:translate(-300px, 10px); opacity: 0.9;}
}

#vsbox {
	border-top: 2px solid #666;
	border-bottom: 2px solid #666;
	position: relative;
	width: 200%;
	height: 90px;
	background: #000;
	box-shadow: 20px 0px 20px #f00,
	           -20px 0px 40px #f0f;
	-webkit-transform: translate(0px, 200px);
	opacity: 0.0;
	-webkit-animation-name: 'vsbox_anime';
	-webkit-animation-duration: 2.5s;
	-webkit-animation-timing-function: cubic-bezier(.01,.89,1,.02);
	-webkit-animation-delay: 0.3s;
}

@-webkit-keyframes vsbox_anime {
	 0% {-webkit-transform:translate(-000px, 150px) rotate(-30deg) scale(1, 0); opacity: 0.0;}
	20% {-webkit-transform:translate(-000px, 150px) rotate(-30deg) scale(1, 0); opacity: 0.0;}
	25% {-webkit-transform:translate(-100px, 150px) rotate(-30deg) scale(1, 1); opacity: 0.9;}
	39% {-webkit-transform:translate(-100px, 150px) rotate(-30deg) scale(1, 1); opacity: 0.0;}
	40% {-webkit-transform:translate(-100px, 155px) rotate(-32deg) scale(1, 1); opacity: 0.9;}
	41% {-webkit-transform:translate(-105px, 143px) rotate(-30deg) scale(1, 1); opacity: 0.0;}
	42% {-webkit-transform:translate(-100px, 155px) rotate(-33deg) scale(1, 1); opacity: 0.9;}
	43% {-webkit-transform:translate(-102px, 150px) rotate(-30deg) scale(1, 1); opacity: 0.0;}
	44% {-webkit-transform:translate(-100px, 145px) rotate(-32deg) scale(1, 1); opacity: 0.9;}
	45% {-webkit-transform:translate(-102px, 150px) rotate(-30deg) scale(1, 1); opacity: 0.0;}
	46% {-webkit-transform:translate(-100px, 155px) rotate(-31deg) scale(1, 1); opacity: 0.9;}
	47% {-webkit-transform:translate(-102px, 150px) rotate(-30deg) scale(1, 1); opacity: 0.0;}
	48% {-webkit-transform:translate(-100px, 155px) rotate(-32deg) scale(1, 1); opacity: 0.9;}
	49% {-webkit-transform:translate(-102px, 150px) rotate(-30deg) scale(1, 1); opacity: 0.0;}
	51% {-webkit-transform:translate(-100px, 155px) rotate(-30deg) scale(1, 1); opacity: 0.9;}
    81% {-webkit-transform:translate(-100px, 150px) rotate(-30deg) scale(1, 1); opacity: 0.9;}
   100% {-webkit-transform:translate(-100px, 150px) rotate(-30deg) scale(1, 1); opacity: 0.0;}
}
