table {
  border-collapse: collapse;
  border-spacing: 0;
  width: 400px;
}

table td {
  width: 50px;

  transition: 0.5s ease-in-out; /* CSS草案 */
  -webkit-transition: 0.5s ease-in-out; /* Safari, Chrome */
  -moz-transition: 0.5s ease-in-out; /* Firefox */
}
table td:hover {
  width: 100px;
}
table td a {
  display: block;
  text-align: center;
  height: 50px;
  text-indent: -99999px;

  transition: 0.5s ease-in-out; /* CSS草案 */
  -webkit-transition: 0.5s ease-in-out; /* Safari, Chrome */
  -moz-transition: 0.5s ease-in-out; /* Firefox */
  border-radius: 5px;
}

table tr {
  transition: 0.5s ease-in-out; /* CSS草案 */
  -webkit-transition: 0.5s ease-in-out; /* Safari, Chrome */
  -moz-transition: 0.5s ease-in-out; /* Firefox */
}
table tr:hover a {
  height: 100px;
}

table td a:link,
table td a:visited {
  color: #333;
  text-decoration: none;
}
table td a:hover,
table td a:active {
  color: #333;
  text-decoration: none;
}


