/* #! scss */

@mixin triangable($color) {
    $s: 100;
    position: absolute;
    left: 0px;
    top: 0px;
	width: 200px;
    height: 200px;
    -webkit-transform-origin: 0% 0%;
    background-image: url("http://dl.dropbox.com/u/3589634/resource/image/misawa.PNG");
}
@mixin triangle($x1, $y1, $x2, $y2, $x3, $y3) {
    $s: 200;
    -webkit-transform: matrix(
        ($x2 - $x1) / $s, ($y2 - $y1) / $s,
        ($x3 - $x1) / $s, ($y3 - $y1) / $s,
        $x1, $y1);
}

#world {
    -webkit-transform: translate(0px, 0px);
}

@-webkit-keyframes dance {
    0% {
        @include triangle(0, 0,  0, 100, 50, 0);
    }
    5% {
        @include triangle(0, 0,  0, 100, 50, 50);
    }
    10% {
        @include triangle(0, 50,  0, 100, 50, 50);
    }
    15% {
        @include triangle(0, 50,  0, 100, 50, 100);
    }
    20% {
        @include triangle(0, 100,  0, 100, 50, 100);
    }
    25% {
        @include triangle(0, 100,  0, 100, 50, 150);
    }
    25% {
        @include triangle(0, 150,  0, 100, 50, 150);
    }
    30% {
        @include triangle(0, 150,  0, 100, 50, 200);
    }
    35% {
        @include triangle(0, 100, 50, 200, 0, 150);
    }
    40% {
        @include triangle(0, 150, 0, 100, 50, 200);
    }
    45% {
        @include triangle(300, 350, 0, 100, 50, 200);
    }
    55% {
        @include triangle(300, 350, 300, 400, 350, 400);
    }
    60% {
        @include triangle(100, 350, 300, 200, 350, 200);
    }
    65% {
        @include triangle(400, 350, 300, 200, 350, 200);
    }
    70% {
        @include triangle(100, 350, 300, 200, 350, 200);
    }
    80% {
        @include triangle(300, 30, 300, 200, 350, 200);
    }
    90% {
        @include triangle(300, 30, 100, 200, 350, 200);
    }
    100% {
        @include triangle(50, 0, 0, 0, 0, 100);
    }
}

div.triangle:nth-child(1) {
  @include triangable(#c99);
  -webkit-animation: dance 3s linear 0s infinite;
}
