source.css:
* {
	margin:0px; padding: 0px;
}

body {
	overflow: hidden;
    font-size: 12px;
	background: #000;
}

.t3 {
	font-family:'ヒラギノ明朝 Pro W3','Hiragino Mincho Pro','ＭＳ Ｐ明朝','MS PMincho',serif;
	font-size: 13px;
	position: absolute;
	padding: 10px;
	left: -245px;
	top: -245px;
	background: #000;
	width: 490px;
	height: 490px;
	color: #f00;
}

.t3:nth-child(1) {
	-webkit-transform: rotateY(0deg) translate3d(0px, 0px, -250px);
}
.t3:nth-child(2) {
	-webkit-transform: rotateY(90deg) translate3d(0px, 0px, -250px);
}
.t3:nth-child(3) {
	-webkit-transform: rotateY(180deg) translate3d(0px, 0px, -250px);
}
.t3:nth-child(4) {
	-webkit-transform: rotateY(270deg) translate3d(0px, 0px, -250px);
}
.t3:nth-child(5) {
	-webkit-transform: rotateX(90deg) translate3d(0px, 0px, -250px);
}
.t3:nth-child(6) {
	-webkit-transform: rotateX(-90deg) translate3d(0px, 0px, -250px);
}

div {
	-webkit-transform-origin: 50% 50%;
	-webkit-transform-style: preserve-3d;
}


#camera {
	position: relative;
	left: 230px;
	top: 230px;
	-webkit-perspective-origin: 0% 0%;
}

#view {
	position: relative;
	left: 0px;
	top: 0px;
	-webkit-perspective-origin: 0% 0%;
}

source.js:
window.onload = (function() {

	var cw = new KeyframesBuilder("cameraWork");
	cw.hook("transform3d", KeyframesUtil.CameraWork3DTransform);

	var kb = new Keyboard();
	// shift
	kb.at(16).enablePrevent();
	// left
	kb.at(37).enablePrevent();
	kb.at("A").enablePrevent();
	// up
	kb.at(38).enablePrevent();
	kb.at("W").enablePrevent();
	// right
	kb.at(39).enablePrevent();
	kb.at("D").enablePrevent();
	// down
	kb.at(40).enablePrevent();
	kb.at("S").enablePrevent();

	var p = [-250, 250, 0];
	var rotateY = 0;
	var rotateX = 0;

	(function() {
		kb.flush();

		if(kb.at(16).pressing) {
			var s = [0, 0, 1].rotatey(rotateY).normalize(5);
			if(kb.at(37).pressing || kb.at("A").pressing) {
				s = s.rotatey((-0.25).toRadian());
				p = p.translate(s);
			}
			if(kb.at(39).pressing || kb.at("D").pressing) {
				s = s.rotatey((0.25).toRadian());
				p = p.translate(s);
			}
			if(kb.at(38).pressing || kb.at("W").pressing) {
				s = s.rotatey((-0.00).toRadian());
				p = p.translate(s);
			}
			if(kb.at(40).pressing || kb.at("S").pressing) {
				s = s.rotatey((-0.50).toRadian());
				p = p.translate(s);
			}
		} else {
			if(kb.at(37).pressing || kb.at("A").pressing) {
				rotateY -= 0.02;
			}
			if(kb.at(39).pressing || kb.at("D").pressing) {
				rotateY += 0.02;
			}
			if(kb.at(38).pressing || kb.at("W").pressing) {
				rotateX += 0.02;
			}
			if(kb.at(40).pressing || kb.at("S").pressing) {
				rotateX -= 0.02;
			}
		}
		var g = [0, 0, 100];
		var u = [0, -100, 0];
		g = g.rotatex(rotateX);
		g = g.rotatey(rotateY);
		u = u.rotatex(rotateX);
		u = u.rotatey(rotateY);
		cw.seek(0).gazeFrom(p, p.translate(g), u);
		cw.fixStyle($("#camera").get(0));
		window.setTimeout(arguments.callee, 33);
	})();
});
