source.css:
body {
    background-color: #000;
    margin: 0px;
    overflow: hidden;
}

a {
  position: absolute;
  display: inline-block;
}


/** ゆらぎ **/
@-webkit-keyframes 'waver' {
    0.0%   {-webkit-transform: translate(5px, 0px) scale(0.7) rotate( 45deg);}
    100.0% {-webkit-transform: translate(-5px, 0px) scale(1) rotate(-45deg) ;}
}

/** パーティクル 重いタイプ **/
@-webkit-keyframes 'rise_h' {
    0.0%   {
      -webkit-transform: translate(200px, 300px);
      font-size: 30;
      z-index: 999;
      opacity: 1;
      text-shadow:
        0px 0px 10px #fff;
      }
    80% {
      text-shadow:
        -1px 0px 20px #99f;
    }
    100.0% {
      -webkit-transform: translate(200px, 230px);
      z-index: 0;
      opacity: 0.2;
      text-shadow:
         8px  8px 2px #003;
    }
}

/** パーティクル 一般タイプ **/
@-webkit-keyframes 'rise_m' {
    0.0%   {
      -webkit-transform: translate(200px, 300px);
      z-index: 999;
      opacity: 1;
      text-shadow:
        0px 0px 10px #fff;
    }
    80% {
      text-shadow:
        -1px 0px 10px #99e;
    }
    100.0% {
      -webkit-transform: translate(200px, 150px);
      z-index: 0;
      opacity: 0.2;
      text-shadow:
       8px  8px 8px #003;
    }
}

/** 粒子 **/
.z:after {
  color: transparent;
  font-size: 20px;
  display: inline-block;
  content: "●";
}



